// Add assets with explicit asset module type
declare module '*?inline';
declare module '*?source';
declare module '*?resource';

// Add font extensions here as needed to get typescript to stop complaining.
declare module '*.otf';
declare module '*.ttf';

// Add image extensions
declare module '*.gif';
declare module '*.png';
declare module '*.jpeg';
declare module '*.svg';
declare module '*.jpg';
declare module '*.ico';
declare module '*.webp';

// PDF, zip
declare module '*.pdf';
declare module '*.zip';

// YAML
declare module '*.yaml';
declare module '*.yml';

// WebAssembly extension
declare module '*.wasm';

// jsx extension
declare module '*.jsx';

// text files
declare module '*.txt';
declare module '*.lvl';
declare module '*.sol';

// Audio files
declare module '*.mp3';
declare module '*.m4a';
declare module '*.wav';

// Other files
declare module '*.tzx';
declare module '*.bas';
